function waktu(){var t=new Date;t.toLocaleString("en-US",{timeZone:"Asia/Makassar"}),setTimeout("waktu()",1e3);var e=t.getHours();e%=12,e=e||12,document.getElementById("waktu").innerHTML=getHari(t.getDay())+", "+t.getDate().toString().padStart(2,"0")+" "+getBulan(t.getMonth())+" "+t.getFullYear()+" "+t.getHours().toString().padStart(2,"0")+":"+t.getMinutes().toString().padStart(2,"0")+":"+t.getSeconds().toString().padStart(2,"0")+" WITA"}function getHari(t){return["Minggu","Senin","Selasa","Rabu","Kamis","Jumat","Sabtu"][t]}function getBulan(t){return["Januari","Februari","Maret","April","Mei","Juni","Juli","Agustus","September","Oktober","November","Desember"][t]}window.setTimeout("waktu()",1e3);
